{"content": {"jsContent": "// Function to convert a string to PascalCase (e.g., 'in progress' -> 'InProgress')
function toPascalCase(str) {
    // Normalize the input by trimming whitespace and converting to lowercase
    const normalizedStr = str.trim().toLowerCase();

    // Check for specific cases
    if (normalizedStr === 'in progress') {
        return 'InProgress';
    }
    if (normalizedStr === 'on air') {
        return 'OnAir';
    }

    // General conversion to PascalCase
    return normalizedStr
        .split(' ')
        .map(word => word.charAt(0).toUpperCase() + word.slice(1))
        .join('');
}



// Function to update the content and classes based on window.result
function updateContent() {
    // Set the heading text
    document.getElementById('heading').innerText = window.result.name || "N/A";

    // Set the priority text
    document.getElementById('priority').innerText = window.result.priority || "N/A";

    // Set the details section
    document.getElementById('code').innerText = `Code: ${window.result.code || "-"}`;
	let areaSquareMeter = window.result.size;
	let areaSquareKm = areaSquareMeter/1000000;
    document.getElementById('areaSize').innerText = `Area size: ${areaSquareKm || "-"} Sq.km`;
    document.getElementById('state').innerText = `State: ${window.result.primaryGeoL2?.geoName || "-"}`;
    document.getElementById('city').innerText = `City: ${window.result.primaryGeoL4?.prettyName || "-"}`;

    // Get the workflowStage element
    const workflowStageElement = document.getElementById('workflowStage');

    // Set the workflow stage text
    const workflowStage = window.result.workflowStage || "N/A";
    workflowStageElement.innerText = workflowStage;

    // Convert the workflow stage to PascalCase to match the CSS class naming convention
    const workflowStageClass = toPascalCase(workflowStage);
    console.log("class : ", workflowStageClass)

    // List of valid workflow stage classes
    const validClasses = ['Planned', 'Proposed', 'OnAir', 'InConstruction', 'InProgress'];

    // Remove existing workflow stage classes
    workflowStageElement.classList.remove(...validClasses);

    // Add the appropriate class based on the workflow stage if it's valid
    if (validClasses.includes(workflowStageClass)) {
        workflowStageElement.classList.add(workflowStageClass);
    }
}


// Call the function to update content
updateContent();
